mapper.insert:
INSERT INTO TB_MEMBER (MEMBER_NO
				 , MEMBER_ID
				 , MEMBER_PWD
				 , MEMBER_NAME
			     , POSITION
				 , TITLE
				 , CHARGE_RATE
				 , EFFECTIVE_DATE
				 , IS_DELETED)
		    VALUES (SEQ_MEM.NEXTVAL
				 , #{memberId}
			     , #{memberPwd}
			     , #{memberName}
			     , #{position}
			     , #{title}
			     , #{chargeRate}
			     , #{effectiveDate}
			     , DEFAULT)

mapper.select:
SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE MEMBER_ID = #{checkId}
